async () => {
  return User_Login.run().then(data => {
    storeValue('role', data.result[0].userInfo.role);
    storeValue('image', data.result[0].userInfo.image);
    delete data.result[0].userInfo;
    Object.keys(data.result[0]).forEach(i => {
      storeValue(i, data.result[0][i]);
    });
    showAlert(data.message, 'success');
    navigateTo('UserDashBoard');
  }).catch(error => {
    if (User_Login.data.message === undefined) {
      showAlert('Too many attempts, SignIn after sometimes', 'warning');
      navigateTo('StartingPage');
    } else showAlert(`${User_Login.data.message}`, 'error');
  });
}
