async () => {
  return forgot_email_Api.run().then(data => {
    showAlert(`Sent Password reset link to ${data.accepted[0]}`, 'success');
    storeValue('email', Input1.text);
  }).catch(error => {
    if (forgot_email_Api.data.message === undefined) {
      showAlert('Too many attempts, SignIn after sometimes', 'warning');
      navigateTo('StartingPage');
    } else showAlert(`${forgot_email_Api.data.message}`, 'error');
  });
}
